mapper.update:
UPDATE users
user_pw = #{password}, email_id = #{emailId}, email_domain = #{emailDomain}, profile_img = #{profileImg},
WHERE user_id = #{userId}

update users
set token = #{token}
where user_id = #{userId}

mapper.select:
select token
from users
where user_id = #{userId}

mapper.update:
update users
set token = #{token, jdbcType=VARCHAR}
where user_id = #{userId}
